import React from "react";
import SearcBar from "./SearchBar";
import MovieList from "./MovieList";



//Functional Component olmaz çünkü dinamik gösterilecek

/*const Filmler = () => {
    return ( <h1>Filmlerim</h1>)
}*/

//Class Component render()) metodunu unutma
class Filmler extends React.Component{
    state_durum = {

        //  filmler_db = [ yerine iki nokta çünkü property şeklinde gelecek
        filmler_db : [
    
            {
                "id": 1,
                "name": "The Flash",
                "rating": 8.3,
                "overview": "333333 Daha fazla bilgi için kullanıcı ve diğer detayalara resmi sayfadan ulaşabilirsiniz",
                "imageUrl": "https://22dakika.org/wp-content/uploads/2015/09/flash-seriesbout.jpg"
            },
            {
                "id": 2,
                "name": "Interstellar",
                "rating": 6.5,
                "overview": "2222 Daha fazla bilgi için kullanıcı ve diğer detayalara resmi sayfadan ulaşabilirsiniz",
                "imageUrl": "https://tr.web.img2.acsta.net/pictures/14/10/09/15/52/150664.jpg"
            },
            {
                "id": 3,
                "name": "Arrow",
                "rating": 7.9,
                "overview": " 333333 Daha fazla bilgi için kullanıcı ve diğer detayalara resmi sayfadan ulaşabilirsiniz",
                "imageUrl": "https://m.media-amazon.com/images/M/MV5BMTI0NTMwMDgtYTMzZC00YmJhLTg4NzMtMTc1NjI4MWY4NmQ4XkEyXkFqcGdeQXVyNTY3MTYzOTA@._V1_.jpg"
            }
        ]
    }

    render(){
        return(
            <div className="container">
                <div className="row">
                   <div className="col-lg-12">
                       <SearcBar/>
                       <div className="display">Dispaly-1</div>
                   </div>
                </div>
                <MovieList/>
            </div>
        )
    }
}

export default Filmler;
