import React from "react";

class MovieList extends React.Component {
    render() {
        return (
            <div className="row">

                {/*props kullanarak yapalım 
            {this.props.filmler.map((flm)=>flm())} */}

                {this.props.filmler.map((flm) => (

                    <div className="col-lg-4">
                        <div className="card mb-4 shadow-sm">
                            <img src={flm.img} className="card-img-top" alt="Sample Movie" />
                            <div className="card-body">
                                <h5 className="card-title">Sample Movie</h5>
                                <p className="card-text">Sample Movie Description</p>
                                <div className="d-flex justify-content-between align-items-center">
                                    <button type="button" className="btn btn-lg btn-outline-danger">Delete</button>
                                    <button type="button" className="btn btn-sm btn-primary">
                                        <h3><span className="badge badge-light">9.0</span></h3>
                                    </button>
                                </div>
                            </div>
                        </div>
                    </div>
                ))}


            </div>
        )
    }


}

export default MovieList;
